mapper.select:
SELECT student_id,student_name,student_code,student_gender,student_phone,student_status,student_grade,
student_remark,student_college_id,student_major_id,student_class_id,student_create_time
FROM sys_student
WHERE student_college_id=#{id}

    AND student_grade=#{grade}

SELECT s.student_id,s.student_name,s.student_code,s.student_gender,s.student_phone,s.student_status,s.student_grade, s.student_remark,s.student_college_id,s.student_major_id,s.student_class_id,s.student_create_time, c.college_name AS student_college_name,m.major_name AS student_major_name,class.class_name AS student_class_name, e.empl_id,e.empl_status,e.empl_work_city,e.empl_work_type,e.empl_status,e.empl_plan,e.empl_intention_city,e.empl_intention_work FROM sys_student s LEFT JOIN sys_college c ON s.student_college_id=c.college_id LEFT JOIN sys_major m ON s.student_major_id=m.major_id LEFT JOIN sys_class class ON s.student_class_id=class.class_id LEFT JOIN stats_empl_info e ON s.student_id=e.empl_student_id WHERE 1=1
AND s.student_id=#{studentId}
AND s.student_college_id=#{collegeId}
AND s.student_major_id=#{majorId}
AND s.student_class_id=#{classId}
AND s.student_name LIKE concat('%',#{name},'%')
AND s.student_grade=#{grade}
AND e.empl_status=#{employment}
AND e.empl_status IS NULL
AND e.empl_status IS NOT NULL
ORDER BY e.empl_status
