mapper.select:
SELECT COUNT(es_id) as people,es_work_city as city FROM employment_status WHERE es_employment=1 AND es_grade=#{grade}
AND es_college_id=#{id} OR es_major_id=#{id} OR es_cls_id=#{id}
GROUP BY city ORDER BY people desc

SELECT COUNT(es_id) as people,es_work_type as type FROM employment_status WHERE es_employment=1 AND es_grade=#{grade}
AND es_college_id=#{id} OR es_major_id=#{id} OR es_cls_id=#{id}
GROUP BY type ORDER BY people desc

SELECT COUNT(es_id) as people,es_plan as plan FROM employment_status WHERE es_employment=0 AND es_grade=#{grade}
AND es_college_id=#{id} OR es_major_id=#{id} OR es_cls_id=#{id}
GROUP BY plan ORDER BY people desc

SELECT COUNT(es_id) as people,es_intention_city as city
FROM employment_status
WHERE es_grade=#{grade}

    AND es_college_id=#{id} OR es_major_id=#{id} OR es_cls_id=#{id}

GROUP BY city
ORDER BY people desc

SELECT COUNT(es_id) as people,es_intention_work as type
FROM employment_status
WHERE es_grade=#{grade}

    AND es_college_id=#{id} OR es_major_id=#{id} OR es_cls_id=#{id}

GROUP BY type
ORDER BY people desc
