mapper.select:
SELECT COUNT(e.es_id) as people,c.city_name as city
FROM employment_status e
LEFT JOIN city c
ON e.es_work_city=c.city_id
WHERE es_employment=1
AND es_grade=#{esGrade}

    AND es_college_id=#{esCollegeId}


    AND es_major_id=#{esMajorId}


    AND es_cls_id=#{esClsId}

GROUP BY city
ORDER BY people desc

SELECT COUNT(e.es_id) as people,w.work_name as type
FROM employment_status e
LEFT JOIN work w
ON e.es_work_type=w.work_id
WHERE e.es_employment=1
AND e.es_grade=#{esGrade}

    AND e.es_college_id=#{esCollegeId}


    AND e.es_major_id=#{esMajorId}


    AND e.es_cls_id=#{esClsId}

GROUP BY type
ORDER BY people desc

SELECT COUNT(es_id) as people,es_plan as plan
FROM employment_status
WHERE es_employment=0
AND es_grade=#{esGrade}

    AND e.es_college_id=#{esCollegeId}


    AND e.es_major_id=#{esMajorId}


    AND e.es_cls_id=#{esClsId}

GROUP BY plan
ORDER BY people desc

SELECT COUNT(e.es_id) as people,c.city_name as city
FROM employment_status e
LEFT JOIN city c
ON e.es_intention_city=c.city_id
WHERE es_employment=1
AND es_grade=#{esGrade}

    AND es_college_id=#{esCollegeId}


    AND es_major_id=#{esMajorId}


    AND es_cls_id=#{esClsId}

GROUP BY city
ORDER BY people desc

SELECT COUNT(e.es_id) as people,w.work_name as type
FROM employment_status e
LEFT JOIN work w
ON e.es_intention_work=w.work_id
WHERE e.es_employment=1
AND e.es_grade=#{esGrade}

    AND e.es_college_id=#{esCollegeId}


    AND e.es_major_id=#{esMajorId}


    AND e.es_cls_id=#{esClsId}

GROUP BY type
ORDER BY people desc
