choose.when:
UPDATE usr
SET usr_pwd=#{pwd}
WHERE usr_id=#{key}

choose.otherwise:
UPDATE student
SET student_pwd=#{pwd}
WHERE student_id=#{key}

choose.when:
SELECT usr_pwd,usr_salt
FROM usr
WHERE usr_id=#{key}
OR usr_account=#{key}

choose.otherwise:
SELECT student_pwd,student_salt
FROM student
WHERE student_id=#{key}
OR student_code=#{key}
